import React, { useState } from 'react';
import { View, TextInput, Button, Text, StyleSheet } from 'react-native';
import axios from 'axios';

const CepSearch = () => {
  const [cep, setCep] = useState('');
  const [cpfUsuario, setCpfUsuario] = useState('');
  const [result, setResult] = useState(null);

  const searchCep = async () => {
    try {
      const response = await axios.get(
        `https://h-apigateway.conectagov.estaleiro.serpro.gov.br/api-cep/v1/consulta/cep/${cep}`,
        {
          headers: {
            Authorization: `Bearer ${process.env.CLIENT_SECRET}`,
            'x-cpf-usuario': cpfUsuario,
          },
        }
      );
      setResult(response.data);
    } catch (error) {
      console.error('Error fetching CEP:', error);
    }
  };

  return (
    <View style={styles.container}>
      <TextInput
        style={styles.input}
        placeholder="Digite o CEP"
        value={cep}
        onChangeText={setCep}
        keyboardType="numeric"
      />
      <TextInput
        style={styles.input}
        placeholder="Digite o CPF do usuÃ¡rio"
        value={cpfUsuario}
        onChangeText={setCpfUsuario}
      />
      <Button title="Buscar CEP" onPress={searchCep} />
      {result && (
        <View style={styles.resultContainer}>
          <Text>CEP: {result.cep}</Text>
          <Text>Logradouro: {result.logradouro}</Text>
          <Text>Bairro: {result.bairro}</Text>
          <Text>Cidade: {result.localidade}</Text>
          <Text>Estado: {result.uf}</Text>
        </View>
      )}
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    justifyContent: 'center',
    padding: 20,
  },
  input: {
    height: 40,
    borderColor: 'gray',
    borderWidth: 1,
    marginBottom: 10,
    paddingLeft: 10,
  },
  resultContainer: {
    marginTop: 20,
  },
});

export default CepSearch;
